            var h = this.getHeight(true);
            if (!jQuery.boxModel) {
                var b2 = mini.getBorders(this._borderEl);
                h = h + b2.top + b2.bottom;
            }
            if (h < 0) h = 0;
            this._borderEl.style.height = h + "px";
